package dayone;

public class SwapNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// WAP to swap two numbers

		

				int a=20;

				int b=30;

				int c;

				

				c = a;

				a = b;

				b = c;

				

				

				System.out.println("Value of a "+a);

				System.out.println("Value of b "+b);
	}

}
