package daytwo;

public class SixthGreaterBetween {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

				int a = 4, b = 7, c = 9;

				

				if(a>b & a>c) {

					System.out.println("a is greater");

				}

				else if(b>a & b>c) {

					System.out.println("b is greater");

				}

				else {

					System.out.println("c is greater");

				}
	}

}
